	constructor(){

		super();

		this.state = {


			global: [],

			refreshing: false



		}

	}
	componentDidMount() {
		const url = 'https://covid19.mathdro.id/api/countries/';
		axios.get(url).then(response => response.data)
		.then((data) => {
			this.setState({ countries: data })
			console.log(this.state.countries)
		})
	}
